mapper.sql:
FROM
	codegroup a
WHERE 1=1
AND ifcgDelNy = #{shcgDelNy}
AND ifcgUseNy = #{shcgUseNy}

	AND a.ifcgRgDate BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.ifcgEditDate BETWEEN #{shDateStart} AND #{shDateEnd}


	AND ifcgSeq = #{shValue}
	AND ifcgName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE CodeGroup
SET
	ifcgName = #{ifcgName}
	,ifcgUseNy = #{ifcgUseNy}
	,ifcgOrder = #{ifcgOrder}
	,ifcgDelNy = #{ifcgDelNy}
	,ifcgDesc = #{ifcgDesc}
	,ifcgEditDate = now()
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}

mapper.delete:
DELETE FROM codegroup
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}
