mapper.sql:
FROM
	rooms d
LEFT JOIN hotel c
	ON d.Hotel_seq = c.hotelSeq
WHERE 1=1
	AND rooms_delNy = #{shRooms_delNy}
	AND status = #{shStatus}

		AND d.roomRgDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND d.roomEditDate BETWEEN #{shDateStart} AND #{shDateEnd}


		AND roomSeq = #{shValue}
		AND roomType LIKE CONCAT('%',#{shValue},'%')
		AND roomDesc LIKE CONCAT('%',#{shValue},'%')
